mapper.select:
SELECT IFNULL(QU_IDX, "") AS QU_IDX , IFNULL(QU_TITLE, "") AS QU_TITLE , IFNULL(QU_CONTENTS, "") AS QU_CONTENTS , IFNULL(QUESTIONER_ID, "") AS QUESTIONER_ID , IFNULL(QUESTIONER_PHONE, "") AS QUESTIONER_PHONE , IFNULL(QUESTION_DT, "") AS QUESTION_DT , IF(QU_ANSWER_YN = 1, QU_ANSWER, "") AS QU_ANSWER , IFNULL(QU_ANSWER_YN, "") AS QU_ANSWER_YN , IFNULL(ANSWERER_ID, "") AS ANSWERER_ID , IFNULL(ANSWER_DT, "") AS ANSWER_DT , IFNULL(IMG_IDX, "") AS IMG_IDX FROM DC_QUESTION
QUESTIONER_ID = #{quIdx}
ORDER BY QU_IDX DESC

mapper.insert:

		INSERT INTO DC_QUESTION(
			QU_TITLE
			, QU_CONTENTS
			, QUESTIONER_ID
			, QUESTIONER_PHONE
			, QUESTION_DT
			, QU_ANSWER_YN
    	)VALUES (
			 #{quTitle}
		    , #{quContents}
		    , #{questionId}
		    , #{questionerPhone}
		    , now()
		    , 0
		)

mapper.select:
SELECT EV_IDX /* 이벤트 IDX */ , IFNULL(EV_IMG_IDX , '' ) AS EV_IMG_IDX /* 이벤트 사진 */ , IFNULL(EV_START_DT , '' ) AS EV_START_DT /* 이벤트 시작기간 */ , IFNULL(EV_END_DT , '' ) AS EV_END_DT /* 이벤트 종료기간 */ , IFNULL(EV_TITLE , '' ) AS EV_TITLE /* 이벤트 제목 */ , IFNULL(EV_CONTENT , '' ) AS EV_CONTENT /* 이벤트 내용 */ , IFNULL(REG_DT , '' ) AS REG_DT /* 생성 시간 */ FROM DC_EVENT
T1.evIdx = #{evIdx}
NOW() >= EV_START_DT AND EV_END_DT >= NOW() ORDER BY REG_DT DESC;
