SELECT T1.QU_IDX AS QU_IDX /* INDEX */ , T1.QR_IDX AS QR_IDX /* 참조키 */ , IFNULL(T1.QUOTE_CODE , '' ) AS QUOTE_CODE /* 견적구분코드 */ , IFNULL(T1.QUOTE_STATUS , '' ) AS QUOTE_STATUS /* 견적상태 */ , CASE WHEN IFNULL(T1.LONGTERM_YN , '' ) = 'N' THEN '단기' ELSE '장기' END AS LONGTERM_YN /* 장단기구분 */ , IFNULL(T1.CARTYPE_CODE_LIST , '' ) AS CARTYPE_CODE_LIST /* 검색 LIST */ , (SELECT MIN(CR_IDX) FROM DC_QUOTE_RENT_COMPANY CR WHERE CR.QU_IDX = T1.QU_IDX AND UR_IDX=#{urIdx}) AS CR_IDX /* CAR IDX */ , IFNULL(T1.CARTYPE_CODE , '' ) AS CARTYPE_CODE /* CAR TYPE */ , IFNULL(T1.RENT_START_DAY, '') AS RENT_START_DAY /* 대여시작일 */ , IFNULL(T1.RENT_END_DAY, '') AS RENT_END_DAY /* 대여시작일 */ , IFNULL(T1.RENT_START_TIME , '' ) AS RENT_START_TIME /* 대여시작시간 */ , IFNULL(T1.RENT_END_TIME , '' ) AS RENT_END_TIME /* 대여종료시간 */ , (SELECT CODE_VALUE FROM DC_COMMON_CODE WHERE RT_CODE='QT' AND P_CODE='QDC' AND CODE = IFNULL(T1.DELIVERY_TYPE_CODE , '' )) AS DELIVERY_TYPE_VALUE /* 배차방법 */ , IFNULL(T1.DELIVERY_TYPE_CODE , '' ) AS DELIVERY_TYPE_CODE , IFNULL(T1.DELIVERY_ADDR , '' ) AS DELIVERY_ADDR /* 배차주소 */ , IFNULL(T1.RETURN_TYPE_CODE , '' ) AS RETURN_TYPE_CODE /* 반차방법 */ , IFNULL(T1.RETURN_ADDR , '' ) AS RETURN_ADDR /* 반차주소 */ , IFNULL(T1.RT_IDX , '' ) AS RT_IDX /* 제휴사 IDX */ , IFNULL(T1.COMPANY_NAME , '' ) AS COMPANY_NAME /* 제휴사명 */ , IFNULL(T1.RENT_FEE , '0' ) AS RENT_FEE /* 대여료 */ , IFNULL(T1.INSURANCE_FEE , '0' ) AS INSURANCE_FEE /* 보험요금 */ , IFNULL(T1.PAYMENT_AMOUNT , '0' ) AS PAYMENT_AMOUNT /* 결제금액 */ , IFNULL(T1.FIRST_DRIVER_NAME , '' ) AS FIRST_DRIVER_NAME /* 운전자1 이름 */ , IFNULL(T1.UL_IDX1 , '' ) AS UL_IDX1 /* 운전자1 IDX */ -- , NVL(T1.SECOND_DRIVER_NAME , '' ) AS SECOND_DRIVER_NAME /* 운전자2 이름 */ , IFNULL(T1.UL_IDX2 , '' ) AS UL_IDX2 /* 운전자2 IDX */ , IFNULL(T1.UR_IDX , '' ) AS UR_IDX /* 유저 IDX */ , IFNULL(T1.REG_ID , '' ) AS REG_ID /* 등록자아이디 */ , IFNULL(DATE_FORMAT(T1.REG_DT , 'YYYY.MM.DD') , ' ' ) AS REG_DT /* 등록일시 */ , IFNULL(T1.MOD_ID , '' ) AS MOD_ID /* 수정자아이디 */ , IFNULL(T1.MOD_DT , '' ) AS MOD_DT /* 수정일 */ , IFNULL(T1.DEL_YN , '' ) AS DEL_YN /* 삭제여부 */ , (SELECT COUNT(1) FROM DC_QUOTE_RENT_COMPANY CR WHERE CR.QUOTE_STATUS = 'QP' AND CR.QU_IDX = T1.QU_IDX) AS TOT_CNT FROM DC_QUOTE_USER T1 LEFT OUTER JOIN DC_RESERVE_MASTER T2 ON T1.QU_IDX = T2.QU_IDX AND T2.UR_IDX = T2.UR_IDX
T1.UR_IDX = #{urIdx}
AND T1.QU_IDX = #{quIdx}
AND T1.QUOTE_STATUS = #{quoteStatus}
AND T1.RENT_END_DAY > DATE_FORMAT(SYSDATE, 'YYYYMMDD')
ORDER BY REG_DT DESC

