(1) FROM DC_QUOTE_RENT_COMPANY CR WHERE CR.QU_IDX = T1.QU_IDX AND CR.QUOTE_STATUS = 'QP' ) AS TOT_CNT , NVL(T3.BRANCH_NAME , '' ) AS BRANCH_NAME /* 지점명 */ , NVL(T3.COMPANY_CONTACT1 , '' ) AS COMPANY_CONTACT1 /* 회원사 연락처 */ FROM DC_QUOTE_USER T1 LEFT OUTER JOIN DC_RESERVE_MASTER T2 ON T1.QU_IDX = T2.QU_IDX AND T2.UR_IDX = T2.UR_IDX LEFT OUTER JOIN DC_RENT_COMPANY T3 ON T1.RT_IDX = T3.RT_IDX AND T2.RT_IDX = T3.RT_IDX
T1.UR_IDX = #{urIdx}
AND T2.QU_IDX IS NULL
AND T1.QU_IDX = #{quIdx}
AND T1.QUOTE_STATUS = #{quoteStatus}
