SELECT T1.RM_IDX AS RM_IDX , T1.QU_IDX AS QU_IDX , T9.QR_IDX AS QR_IDX , NVL(T1.CR_IDX, '') AS CR_IDX , NVL(T1.RESERVE_TYPE_CODE, '') AS RESERVE_TYPE_CODE , NVL(T1.RESERVE_STATUS_CODE, '') AS RESERVE_STATUS_CODE , NVL(T1.LONGTERM_YN, '') AS LONGTERM_YN , NVL(T1.UR_IDX, '') AS UR_IDX , NVL(T1.RESERVE_USER_NAME, '') AS RESERVE_USER_NAME , NVL(T1.RENT_START_DAY, '') AS RENT_START_DAY , NVL(T1.RENT_END_DAY, '') AS RENT_END_DAY , NVL(T1.RENT_START_TIME, '') AS RENT_START_TIME , NVL(T1.RENT_END_TIME, '') AS RENT_END_TIME , NVL(DATE_FORMAT(TO_DATE(T1.RENT_START_DAY, 'YYYY-MM-DD'), 'YYYY.MM.DD'), '' ) AS RENT_START_DAY , DATE_FORMAT(TO_DATE(T1.RENT_START_DAY, 'YYYY-MM-DD'), 'DY','NLS_DATE_LANGUAGE=korean') RENT_START_DOW , NVL(DATE_FORMAT(TO_DATE(T1.RENT_END_DAY, 'YYYY-MM-DD'),'YYYY.MM.DD') , '' ) AS RENT_END_DAY , DATE_FORMAT(TO_DATE(T1.RENT_END_DAY, 'YYYY-MM-DD'), 'DY','NLS_DATE_LANGUAGE=korean') RENT_END_DOW , NVL(T1.RENT_START_TIME , '' ) AS RENT_START_TIME , NVL(DATE_FORMAT(TO_DATE(T1.RENT_START_TIME, 'HH24:MI'), 'AM HH:MI', 'nls_date_language=american') , '' ) AS RENT_START_12TIME , NVL(T1.RENT_END_TIME , '' ) AS RENT_END_TIME , NVL(DATE_FORMAT(TO_DATE(T1.RENT_END_TIME, 'HH24:MI'), 'AM HH:MI', 'nls_date_language=american') , '' ) AS RENT_END_12TIME , NVL(T1.DELIVERY_TYPE_CODE, '') AS DELIVERY_TYPE_CODE , NVL(T1.DELIVERY_ADDR, '') AS DELIVERY_ADDR , NVL(T1.RETURN_TYPE_CODE, '') AS RETURN_TYPE_CODE , NVL(T1.RETURN_ADDR, '') AS RETURN_ADDR , NVL(T6.YEAR, '') AS YEAR , NVL(T6.MODEL_NAME, '') AS MODEL_NAME , NVL(T6.MILEAGE, '') AS MILEAGE , NVL(T6.MODEL_DETAIL_NAME, '') AS MODEL_DETAIL_NAME , NVL(GET_COMMON_CODE_VALUE('FL',T6.FUEL_CODE) , '' ) AS FUEL_CODE , NVL(T6.CARTYPE_CODE, '') AS CARTYPE_CODE , NVL(T1.RT_IDX, '') AS RT_IDX , (SELECT COMPANY_NAME FROM DC_RENT_COMPANY WHERE RT_IDX=T9.RT_IDX) AS COMPANY_NAME , NVL(T1.RESERVE_DATE , '') AS RESERVE_DATE , NVL(T1.PAYMENT_DATE , '') AS PAYMENT_DATE , NVL(T1.CAR_DEPOSIT , '0') AS CAR_DEPOSIT , NVL(T1.RENT_FEE , '0') AS RENT_FEE , NVL(T1.INSURANCE_FEE , '0') AS INSURANCE_FEE , NVL(T1.DISCOUNT_FEE , '0') AS DISCOUNT_FEE , NVL( NVL(T1.RENT_FEE, '0') + NVL(T1.INSURANCE_FEE, '0'), '0') AS PAYMENT_AMOUNT , NVL(T1.CANCEL_FEE , '0') AS CANCEL_FEE , NVL(T1.CANCEL_AMOUNT , '0') AS CANCEL_AMOUNT , NVL(T1.CANCEL_REASON , '') AS CANCEL_REASON , NVL(T1.FIRST_DRIVER_NAME, '') AS FIRST_DRIVER_NAME , NVL(T1.UL_IDX1, '') AS UL_IDX1 -- , NVL(T1.SECOND_DRIVER_NAME, '') AS SECOND_DRIVER_NAME , NVL(T1.UL_IDX2, '') AS UL_IDX2 , NVL(T1.RESERVE_M_ETC, '') AS RESERVE_M_ETC , NVL(T1.REG_ID, '') AS REG_ID , NVL(T1.REG_DT, '') AS REG_DT , NVL(T1.MOD_ID, '') AS MOD_ID , NVL(T1.MOD_DT, '') AS MOD_DT , NVL(T1.DEL_YN, '') AS DEL_YN , NVL(T1.RENT_FEE , '0' ) AS RENT_FEE /* 대여료 */ , NVL(T1.INSURANCE_FEE , '0' ) AS INSURANCE_FEE /* 보험요금 */ FROM DC_RESERVE_MASTER T1 LEFT OUTER JOIN DC_USER_INFO T5 ON T1.UR_IDX = T5.UR_IDX LEFT OUTER JOIN DC_CAR_INFO T6 ON T1.CR_IDX = T6.CR_IDX LEFT OUTER JOIN DC_CAR_INFO_INSURANCE T7 ON T1.CR_IDX = T7.CR_IDX LEFT OUTER JOIN DC_QUOTE_USER T8 ON T1.UR_IDX= T8.UR_IDX AND T1.UR_IDX = T8.UR_IDX AND T1.QU_IDX = T8.QU_IDX LEFT OUTER JOIN DC_QUOTE_RENT_COMPANY T9 ON T1.UR_IDX = T9.UR_IDX AND T8.UR_IDX = T9.UR_IDX AND T1.RT_IDX = T9.RT_IDX AND T1.QU_IDX = T9.QU_IDX AND T8.QR_IDX = T9.QR_IDX
T1.UR_IDX= #{urIdx}
AND T8.QUOTE_STATUS = #{reserveStatusCode}
ORDER BY T1.REG_DT DESC

