, NVL(T4.CAR_DRIVE_LIMIT , '' ) AS CAR_DRIVE_LIMIT /* 주행거리제한 */ , NVL(T4.AGE_LIMIT , '' ) AS AGE_LIMIT /* 대여연령제한 */ , NVL(T4.GARAGE_ADDR , '' ) AS GARAGE_ADDR /* 차고지주소 */ , NVL(T4.CAR_ETC , '' ) AS CAR_ETC /* 비고 */ , NVL(GET_COMMON_CODE_VALUE('MF',T4.MANUFACTURER_CODE) , '' ) AS MANUFACTURER_NAME /* 제조사명 */ , NVL(T5.INSURANCE_FEE , '0' ) AS INSURANCE_FEE /* 보험테이블 보험요금*/ , NVL(T5.CAR_DAMAGE_COVER , '0' ) AS CAR_DAMAGE_COVER /* 자차보상금액*/ , NVL(T5.ONSELF_DAMAGE_COVER , '0' ) AS ONSELF_DAMAGE_COVER /* 자손보상금액*/ , NVL(T5.PERSONAL_COVER , '0' ) AS PERSONAL_COVER /* 대인보상금액*/ , NVL(T5.PROPERTY_DAMAGE_COVER, '0' ) AS PROPERTY_DAMAGE_COVER /* 대물보상금액*/ , NVL(T5.DRIVE_CAREER_LIMIT , '0' ) AS DRIVE_CAREER_LIMIT /* 운전경력제한*/ , NVL(T5.INSURANCE_COPAYMENT , '0' ) AS INSURANCE_COPAYMENT /* 본인부담금 */ , NVL(T3.CAR_DEPOSIT , '0' ) AS CAR_DEPOSIT /* 장기대여 보증금*/ FROM DC_QUOTE_USER T1 LEFT OUTER JOIN DC_USER_INFO T2 ON T1.UR_IDX = T2.UR_IDX LEFT OUTER JOIN DC_QUOTE_RENT_COMPANY T3 ON T1.QU_IDX = T3.QU_IDX LEFT OUTER JOIN DC_CAR_INFO T4 ON T3.CR_IDX = T4.CR_IDX LEFT OUTER JOIN DC_CAR_INFO_INSURANCE T5 ON T4.CR_IDX = T5.CR_IDX
