SELECT NVL(T1.UL_IDX1 , '' ) AS UL_IDX1 /* 운전자1 IDX */ -- , NVL(T1.SECOND_DRIVER_NAME , '' ) AS SECOND_DRIVER_NAME /* 운전자2 이름 */ , NVL(T1.UL_IDX2 , '' ) AS UL_IDX2 /* 운전자2 IDX */ , NVL(T1.UR_IDX , '' ) AS UR_IDX /* 유저 IDX */ , NVL(T2.USER_ID , '' ) AS USER_ID /* 아이디 */ , NVL(T2.USER_NAME , '' ) AS USER_NAME /* 이름 */ , NVL(T2.USER_BIRTHDAY , '' ) AS USER_BIRTHDAY /* 생년월일 */ , NVL(T2.USER_GENDER , '' ) AS USER_GENDER /* 성별 */ , NVL(T2.USER_CONTACT1 , '' ) AS USER_CONTACT1 /* 연락처 1 */ , NVL(T2.USER_CONTACT2 , '' ) AS USER_CONTACT2 /* 연락처 2 */ , NVL(T3.RENT_FEE , '0' ) AS RENT_FEE /* 대여료 */ , NVL(T3.INSURANCE_FEE , '0' ) AS INSURANCE_FEE /* 보험료 */ , NVL(T3.PAYMENT_AMOUNT , '0' ) AS PAYMENT_AMOUNT /* 결제금액 */ , NVL(T4.MD_IDX , '' ) AS MD_IDX /* 모델상세idx */ , NVL(T4.MODEL_NAME , '' ) AS MODEL_NAME /* 모델명 */ , NVL(T4.MODEL_DETAIL_NAME , '' ) AS MODEL_DETAIL_NAME /* 모델상세명 */ , NVL(GET_COMMON_CODE_VALUE('FL',T4.FUEL_CODE) , '' ) AS FUEL_CODE /* 연료구분 */ , NVL(T4.TRANSMISSION_CODE , '' ) AS TRANSMISSION_CODE /* 변속기구분code */ , NVL(T4.DRIVE_TYPE_CODE , '' ) AS DRIVE_TYPE_CODE /* 구동방식구분code */ , NVL(T4.CARTYPE_CODE , '' ) AS CARTYPE_CODE /* 차종code */ , NVL(T4.DRIVE_LICENSE_CODE , '' ) AS DRIVE_LICENSE_CODE /* 면허구분code */ , NVL(T4.MANUFACTURER_CODE , '' ) AS MANUFACTURER_CODE /* 제조사code */ , NVL(T4.COLOR_NAME , '') AS COLOR_NAME /* 색상code */ , NVL(GET_COMMON_CODE_VALUE('CR',T4.FUEL_CODE) , '' ) AS FUEL_NAME /* 연료구분명 */ , NVL(T4.DISPLACEMENT , '' ) AS DISPLACEMENT /* 배기량 */ , NVL(T4.YEAR , '' ) AS YEAR /* 연식 */ , NVL(T4.MILEAGE , '' ) AS MILEAGE /* 주행거리 */ , NVL(T4.MAXIMUM_PASSENGER , '' ) AS MAXIMUM_PASSENGER /* 승차인원 */ , NVL(T4.PY_IDX , '' ) AS PY_IDX /* 요금idx */ , NVL(T4.DAILY_STANDARD_PAY , '0' ) AS DAILY_STANDARD_PAY /* 단기요금 */ , NVL(T4.DAILY_STANDARD_PAY , '0' ) AS DAILY_STANDARD_PAY /* 장기요금 */ , NVL(T4.LONGTERM_DEPOSIT , '' ) AS LONGTERM_DEPOSIT /* 장기대여보증금 */ , NVL(T4.CAR_STATUS_CODE , '' ) AS CAR_STATUS_CODE /* 차량상태code */ , NVL(T4.RESERVE_ABLE_YN , '' ) AS RESERVE_ABLE_YN /* 차량예약가능여부code */ , NVL(T4.CAR_REG_DT , '' ) AS CAR_REG_DT /* 차량등록일 */ , NVL(T4.CAR_NUMBER , '' ) AS CAR_NUMBER /* 차량번호 */ , NVL(T4.CAR_CHASSIS_NUMBER , '' ) AS CAR_CHASSIS_NUMBER /* 차대번호 */ --, NVL(T4.IMG_IDX , '' ) AS IMG_IDX /* 이미지idx */ , NVL(T4.CAR_DRIVE_LIMIT , '' ) AS CAR_DRIVE_LIMIT /* 주행거리제한 */ , NVL(T4.AGE_LIMIT , '' ) AS AGE_LIMIT /* 대여연령제한 */ , NVL(T4.GARAGE_ADDR , '' ) AS GARAGE_ADDR /* 차고지주소 */ , NVL(T4.CAR_ETC , '' ) AS CAR_ETC /* 비고 */ , NVL(GET_COMMON_CODE_VALUE('MF',T4.MANUFACTURER_CODE) , '' ) AS MANUFACTURER_NAME /* 제조사명 */ , NVL(T5.INSURANCE_FEE , '0' ) AS INSURANCE_FEE /* 보험테이블 보험요금*/ , NVL(T5.CAR_DAMAGE_COVER , '0' ) AS CAR_DAMAGE_COVER /* 자차보상금액*/ , NVL(T5.ONSELF_DAMAGE_COVER , '0' ) AS ONSELF_DAMAGE_COVER /* 자손보상금액*/ , NVL(T5.PERSONAL_COVER , '0' ) AS PERSONAL_COVER /* 대인보상금액*/ , NVL(T5.PROPERTY_DAMAGE_COVER, '0' ) AS PROPERTY_DAMAGE_COVER /* 대물보상금액*/ , NVL(T5.DRIVE_CAREER_LIMIT , '' ) AS DRIVE_CAREER_LIMIT /* 운전경력제한*/ , NVL(T5.INSURANCE_COPAYMENT , '0' ) AS INSURANCE_COPAYMENT /* 본인부담금 */ , NVL(T1.FIRST_DRIVER_NAME , '' ) AS FIRST_DRIVER_NAME , T1.QU_IDX AS QU_IDX /* INDEX */ , T3.QR_IDX AS QR_IDX /* 참조키 */ , NVL(T1.QUOTE_CODE , '' ) AS QUOTE_CODE /* 견적구분코드 */ , NVL(T1.QUOTE_STATUS , '' ) AS QUOTE_STATUS /* 견적상태 */ , CASE WHEN NVL(T1.LONGTERM_YN , '' ) = 'N' THEN '단기' ELSE '장기' END AS LONGTERM_YN /* 장단기구분 */ , NVL(T1.CARTYPE_CODE_LIST , '' ) AS CARTYPE_CODE_LIST /* 검색 LIST */ , NVL(T3.CR_IDX , '' ) AS CR_IDX /* CAR IDX */ , NVL(T1.CARTYPE_CODE , '' ) AS CARTYPE_CODE /* CAR TYPE */ , NVL(T1.RENT_START_DAY, '') AS RENT_START_DAY /* 대여시작일자 */ , NVL(T1.RENT_END_DAY, '') AS RENT_END_DAY /* 대여종료일자 */ , NVL(T1.RENT_START_TIME , '' ) AS RENT_START_TIME /* 대여시작시간 */ , NVL(T1.RENT_END_TIME , '' ) AS RENT_END_TIME /* 대여종료시간 */ , (SELECT CODE_VALUE FROM DC_COMMON_CODE WHERE RT_CODE='QT' AND P_CODE='QDC' AND CODE = NVL(T1.DELIVERY_TYPE_CODE , '' )) AS DELIVERY_TYPE_VALUE /* 배차방법 */ , NVL(T1.DELIVERY_TYPE_CODE , '' ) AS DELIVERY_TYPE_CODE , NVL(T1.DELIVERY_ADDR , '' ) AS DELIVERY_ADDR /* 배차주소 */ , NVL(T1.RETURN_TYPE_CODE , '' ) AS RETURN_TYPE_CODE /* 반차방법 */ , NVL(T1.RETURN_ADDR , '' ) AS RETURN_ADDR /* 반차주소 */ , NVL(T1.RT_IDX , '' ) AS RT_IDX /* 제휴사 IDX */ , NVL(T6.COMPANY_NAME , '' ) AS COMPANY_NAME /* 제휴사명 */ , NVL(T1.REG_ID , '' ) AS REG_ID /* 등록자아이디 */ , NVL(DATE_FORMAT(T1.REG_DT , 'YY.MM.DD') , ' ' ) AS REG_DT /* 등록일시 */ , NVL(T1.MOD_ID , '' ) AS MOD_ID /* 수정자아이디 */ , NVL(T1.MOD_DT , '' ) AS MOD_DT /* 수정일 */ , NVL(T1.DEL_YN , '' ) AS DEL_YN /* 삭제여부 */ , NVL(T3.MONTHLY_FEE , '' ) AS MONTHLY_FEE /* 개월가격 */ , NVL(T3.DAILY_FEE , '' ) AS DAILY_FEE /* 일가격 */ , (SELECT COUNT(1) FROM DC_QUOTE_RENT_COMPANY CR WHERE T3.QUOTE_STATUS = #{quoteStatus} AND CR.QU_IDX = T1.QU_IDX ) AS TOT_CNT , NVL(T3.CAR_DEPOSIT , '0') AS CAR_DEPOSIT /* 보증금 */ , NVL(T6.COMPANY_ADDRESS , '' ) AS COMPANY_ADDRESS /* 제휴사주소 */ , NVL(T3.MONTHLY_FEE , '' ) AS MONTHLY_FEE /* 월대여료 */ , NVL(T3.DAILY_FEE , '' ) AS DAILY_FEE /* 일대여료 */ , NVL(T6.BRANCH_NAME , '' ) AS BRANCH_NAME /* 지점명 */ , NVL(T6.COMPANY_CONTACT1 , '' ) AS COMPANY_CONTACT1 /* 회원사 연락처 */ FROM DC_QUOTE_USER T1 LEFT OUTER JOIN DC_USER_INFO T2 ON T1.UR_IDX = T2.UR_IDX LEFT OUTER JOIN DC_QUOTE_RENT_COMPANY T3 ON T1.QU_IDX = T3.QU_IDX LEFT OUTER JOIN DC_CAR_INFO T4 ON T3.CR_IDX = T4.CR_IDX LEFT OUTER JOIN DC_CAR_INFO_INSURANCE T5 ON T3.CR_IDX = T5.CR_IDX AND T4.CR_IDX = T5.CR_IDX LEFT OUTER JOIN DC_RENT_COMPANY T6 ON T3.RT_IDX = T6.RT_IDX
T1.UR_IDX = #{urIdx}
AND T1.QU_IDX = #{quIdx}
AND T3.QUOTE_STATUS = #{quoteStatus}
ORDER BY T3.PAYMENT_AMOUNT
ORDER BY T3.REG_DT DESC

SELECT NVL(T1.LONGTERM_YN , '' ) AS LONGTERM_YN /* 장단기구분 */ , NVL(T1.UR_IDX , '' ) AS UR_IDX /* 회원idx*/ , NVL(T2.USER_NAME , '' ) AS USER_NAME /* 예약자명*/ , NVL(T1.RENT_START_DAY , '' ) AS RENT_START_DAY /* 대여시작일 */ , NVL(T1.RENT_END_DAY , '' ) AS RENT_END_DAY /* 대여종료일 */ , NVL(T1.RENT_START_TIME , '' ) AS RENT_START_TIME /* 대여시작시간 */ , NVL(T1.RENT_END_TIME , '' ) AS RENT_END_TIME /* 대여종료시간 */ , (SELECT CODE_VALUE FROM DC_COMMON_CODE WHERE RT_CODE='QT' AND P_CODE='QDC' AND CODE = NVL(T1.DELIVERY_TYPE_CODE , '' )) AS DELIVERY_TYPE_VALUE /* 배차방법 */ , NVL(T1.DELIVERY_TYPE_CODE , '' ) AS DELIVERY_TYPE_CODE /* 배차방법 */ , NVL(T1.DELIVERY_ADDR , '' ) AS DELIVERY_ADDR /* 배차주소 */ , NVL(T1.RETURN_TYPE_CODE , '' ) AS RETURN_TYPE_CODE /* 반차방법 */ , NVL(T1.RETURN_ADDR , '' ) AS RETURN_ADDR /* 반차주소 */ , NVL(T4.CR_IDX , '' ) AS CR_IDX /* 차량 IDX */ , NVL(T4.CARTYPE_CODE , '' ) AS CARTYPE_CODE /* 차종 CODE */ , NVL(T6.RT_IDX , '' ) AS RT_IDX /* 제휴사IDX */ , NVL(T6.COMPANY_NAME , '' ) AS COMPANY_NAME /* 제휴사명 */ , NVL(T6.COMPANY_ADDRESS , '' ) || NVL(T6.COMPANY_ADDRESS_DETAIL , '' ) AS COMPANY_ADDRESS /* 제휴사 주소 */ , NVL(T1.REG_DT , '' ) AS RESERVE_DATE /* 예약일 */ --, NVL(DATE_FORMAT(TO_DATE(T1.REG_DT, 'YYYY-MM-DD'), 'YYYY.MM.DD'), '' ) AS RESERVE_DATE /* 예약일 */ -- RESERVE_DATE 예약일 -- PAYMENT_DATE 결제일 -- CAR_DEPOSIT 보증금액 , NVL(T4.LONGTERM_DEPOSIT , '0' ) AS CAR_DEPOSIT -- 보증금액(LONGTERM_DEPOSIT) , NVL(T3.RENT_FEE , '0' ) AS RENT_FEE --대여금액 , NVL(T3.INSURANCE_FEE , '0' ) AS INSURANCE_FEE -- INSURANCE_FEE 보험금액 -- DISCOUNT_FEE 할인금액 -- PAYMENT_AMOUNT 결제금액 -- 결제IDX PD_IDX , NVL(T1.FIRST_DRIVER_NAME , '' ) AS FIRST_DRIVER_NAME /* 운전자1 이름 */ , NVL(T1.UL_IDX1 , '' ) AS UL_IDX1 /* 운전자1 IDX */ -- , NVL(T1.SECOND_DRIVER_NAME , '' ) AS SECOND_DRIVER_NAME /* 운전자2 이름 */ , NVL(T1.UL_IDX2 , '' ) AS UL_IDX2 /* 운전자2 IDX */ , NVL(T1.QU_IDX , '' ) AS QU_IDX /* 견적IDX */ , NVL(T3.QR_IDX , '' ) AS QR_IDX /* 회원사견적IDX */
FROM DC_QUOTE_USER T1 LEFT OUTER JOIN DC_USER_INFO T2 ON T1.UR_IDX = T2.UR_IDX LEFT OUTER JOIN DC_QUOTE_RENT_COMPANY T3 ON T1.QU_IDX = T3.QU_IDX LEFT OUTER JOIN DC_CAR_INFO T4 ON T3.CR_IDX = T4.CR_IDX LEFT OUTER JOIN DC_CAR_INFO_INSURANCE T5 ON T3.CR_IDX = T5.CR_IDX AND T4.CR_IDX = T5.CR_IDX LEFT OUTER JOIN DC_RENT_COMPANY T6 ON T3.RT_IDX = T6.RT_IDX
T1.UR_IDX = #{urIdx}
AND T1.QUOTE_STATUS = #{quoteStatus}
AND T1.QU_IDX = #{quIdx}
AND T3.QR_IDX = #{qrIdx}
