mapper.select:
SELECT IFNULL(UR_IDX , '' ) AS UR_IDX /* 회원IDX */ , IFNULL(USER_ID , '' ) AS USER_ID /* 회원ID */ , IFNULL(USER_PASSWORD , '' ) AS USER_PASSWORD /* 비밀번호 */ , IFNULL(USER_DUP_INFO , '' ) AS USER_DUP_INFO /* 본인인증id */ , IFNULL(USER_STATUS_CODE , '' ) AS USER_STATUS_CODE /* 회원상태코드 */ , IFNULL(USER_NAME , '' ) AS USER_NAME /* 이름 */ , IFNULL(USER_BIRTHDAY , '' ) AS USER_BIRTHDAY /* 생년월일 */ , IFNULL(USER_CONTACT1 , '' ) AS USER_CONTACT1 /* 연락처1 */ , IFNULL(USER_CONTACT2 , '' ) AS USER_CONTACT2 /* 연락처2 */ , IFNULL(USER_GENDER , '' ) AS USER_GENDER /* 성별CODE */ , IFNULL(USER_ZIPCODE , '' ) AS USER_ZIPCODE /* 우편번호 */ , IFNULL(USER_ADDRESS , '' ) AS USER_ADDRESS /* 주소 */ , IFNULL(USER_ADDRESS_DETAIL , '' ) AS USER_ADDRESS_DETAIL /* 상세주소 */ , IFNULL(USER_IDENTITY_AUTH_YN , '' ) AS USER_IDENTITY_AUTH_YN /* 본인인증여부 */ , IFNULL(USER_IDENTITY_AUTH_DATE , '' ) AS USER_IDENTITY_AUTH_DATE /* 인증일자 */ , IFNULL(USER_CI , '' ) AS USER_CI /* USER_CI */ , IFNULL(USER_DI , '' ) AS USER_DI /* USER_DI */ , IFNULL(USER_NATIONAL_CODE , '' ) AS USER_NATIONAL_CODE /* 내/외국인 구분 */ , IFNULL(USER_CERT_TYPE , '' ) AS USER_CERT_TYPE /* 인증서유형 */ , IFNULL(USER_GRADE_CODE , '' ) AS USER_GRADE_CODE /* 회원등급 */ , IFNULL(USER_ROLE , '' ) AS USER_ROLE /* 멤버역할(ROLE) */ , IFNULL(RT_IDX , '' ) AS RT_IDX /* 회원사DIX */ , IFNULL(USER_GROUP_CODE , '' ) AS USER_GROUP_CODE /* 회원분류코드 */ , IFNULL(USER_LICENSE_OWN_YN , '' ) AS USER_LICENSE_OWN_YN /* 면허소유여부 */ , IFNULL(USER_PAY_REGISTER_YN , '' ) AS USER_PAY_REGISTER_YN /* 결제수단등록여부 */ , IFNULL(CORPORATION_IDX , '' ) AS CORPORATION_IDX /* 법인IDX */ , IFNULL(USER_PUSH_AGREE_YN , '' ) AS USER_PUSH_AGREE_YN /* PUSH동의여부 */ , IFNULL(JOIN_CHANNEL , '' ) AS JOIN_CHANNEL /* 가입경로 */ , IFNULL(SOCIAL_LOGIN_PATH , '' ) AS SOCIAL_LOGIN_PATH /* 연동로그인경로 */ , IFNULL(SOCIAL_LOGIN_EMAIL , '' ) AS SOCIAL_LOGIN_EMAIL /* 연동로그인메일 */ , IFNULL(USER_WITHDRAW_DATE , '' ) AS USER_WITHDRAW_DATE /* 회원탈퇴일시 */ , IFNULL(LOGIN_FAIL_COUNT , 0 ) AS LOGIN_FAIL_COUNT /* 로그인시도횟수 */ , DATE_FORMAT(REG_DT , '%Y%m%d') AS REG_DT /* 등록일시 */ , IFNULL(REG_ID , '' ) AS REG_ID /* 등록자 */ , DATE_FORMAT(MOD_DT , '%Y%m%d') AS MOD_DT /* 수정일시 */ , IFNULL(MOD_ID , '' ) AS MOD_ID /* 수정자 */ FROM DC_USER_INFO
USER_ID = #{userId}
AND USER_PASSWORD = #{userPassword}
AND USER_DUP_INFO = #{userDupInfo}
AND USER_CONTACT1 = #{userContact1}
AND USER_CI = #{userCi}
AND UR_IDX = #{urIdx}
AND USER_NAME = #{userName}

