mapper.select:

				SELECT 
				      COUNT(USER_ID) AS cntUser
					FROM   DC_USER_INFO



                 USER_ID = #{userId} 


                AND USER_PASSWORD = #{userPassword}


                AND USER_CONTACT1 = #{userContact1} 


                AND USER_CI = #{userCi} 


                AND UR_IDX = #{urIdx} 

             AND 'WD' <> IFNULL(USER_STATUS_CODE, ' ')

